                <div className="detail-item">
                  <span className="detail-label">üë§ Recipient</span>
                  <span className="detail-value">{shipment.recipientName || 'dev'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üìç Delivery Address</span>
                  <span className="detail-value">{shipment.deliveryAddress || 'mumbai'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üì¶ Package Type</span>
                  <span className="detail-value">{shipment.packageType || 'Documents'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">‚öñÔ∏è Weight</span>
                  <span className="detail-value">{shipment.weight || '5'} kg</span>
                </div>
                {shipment.specialInstructions && (
                  <div className="detail-item">
                    <span className="detail-label">üìù Special Instructions</span>
                    <span className="detail-value">{shipment.specialInstructions || 'fragile'}</span>
                  </div>
                )}
                <div className="detail-item">
                  <span className="detail-label">üìÖ Created</span>
                  <span className="detail-value">{new Date(shipment.createdAt).toLocaleString()}</span>
                </div>
                {shipment.currentAddress && (
                  <div className="detail-item">
                    <span className="detail-label">üó∫Ô∏è Current Location</span>
                    <span className="detail-value">{shipment.currentAddress}</span>
                  </div>
                )}
              </div>

              <div className="status-section">
                <h3>Current Status</h3>
