import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import apiClient from '../api/apiClient';
import './Dashboard.css';

const DirectTrackShipment = () => {
  const { trackingId } = useParams();
  const [shipment, setShipment] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchShipment = async () => {
      if (!trackingId) {
        setError('No tracking ID provided');
        setLoading(false);
        return;
      }

      try {
        const response = await apiClient.get(`/shipment/tracking/${trackingId}`);
        setShipment(response.data);
      } catch (err) {
        if (err.response?.status === 404) {
          setError('Shipment not found. Please check your tracking ID.');
        } else {
          setError(err.response?.data?.message || 'An error occurred while tracking the shipment.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchShipment();
  }, [trackingId]);

  const getStatusEmoji = (status) => {
    switch (status) {
      case 'Delivered':
        return 'üéØ';
      case 'In Transit':
        return 'üöö';
      case 'Pending':
        return 'üì¶';
      default:
        return 'üìã';
    }
  };

  const getStatusClass = (status) => {
    return status ? status.toLowerCase().replace(' ', '-') : 'pending';
  };

  if (loading) {
    return (
      <div className="modern-dashboard">
        <div className="dashboard-hero" style={{ background: 'linear-gradient(135deg, #3a1c71, #d76d77, #ffaf7b)' }}>
          <div className="hero-content">
            <h1>üîç Tracking Your Shipment</h1>
            <p>We're looking up your package with tracking ID: <strong>{trackingId}</strong></p>
          </div>
        </div>
        <div className="tracking-loading">
          <div className="loading-animation">
            <div className="package-icon">üì¶</div>
            <div className="loading-text">
              <h3>Fetching Shipment Details</h3>
              <p>Please wait while we gather the latest information about your package</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="modern-dashboard">
        <div className="dashboard-hero" style={{ background: 'linear-gradient(135deg, #3a1c71, #d76d77, #ffaf7b)' }}>
          <div className="hero-content">
            <h1>‚ùå Tracking Error</h1>
            <p>We couldn't find information for tracking ID: <strong>{trackingId}</strong></p>
          </div>
        </div>
        <div className="modern-error">
          <span>‚ö†Ô∏è</span>
          {error}
        </div>
        <div className="help-section">
          <div className="help-card">
            <h3>üí° Need Help?</h3>
            <div className="help-tips">
              <div className="tip">
                <span className="tip-icon">‚úì</span>
                <span>Double-check your tracking ID for typos</span>
              </div>
              <div className="tip">
                <span className="tip-icon">‚úì</span>
                <span>Tracking IDs are usually in the format PT-XXXX-XXXXXX</span>
              </div>
              <div className="tip">
                <span className="tip-icon">‚úì</span>
                <span>Recently created shipments may take a few minutes to appear in our system</span>
              </div>
            </div>
            <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
              <Link to="/track-shipment" className="modern-btn primary">
                Try Manual Tracking
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modern-dashboard">
      <div className="dashboard-hero" style={{ background: 'linear-gradient(135deg, #3a1c71, #d76d77, #ffaf7b)' }}>
        <div className="hero-content">
          <h1>{getStatusEmoji(shipment.status)} Shipment Tracked</h1>
          <p>
            Tracking ID: <strong>{trackingId}</strong> ‚Ä¢ 
            Status: <strong>{shipment.status}</strong>
          </p>
        </div>
        {shipment.status === 'Delivered' && (
          <div className="hero-stats">
            <div className="stat-item">
              <div className="stat-number">‚úÖ</div>
              <div className="stat-label">Delivered Successfully</div>
            </div>
          </div>
        )}
      </div>

      {shipment && (
        <div className="tracking-results">
          <div className="result-card">
            <div className="result-header">
              <div className="success-icon">{getStatusEmoji(shipment.status)}</div>
              <div className="success-text">
                <h2>Shipment Information</h2>
                <p>Current status: <strong>{shipment.status}</strong></p>
              </div>
            </div>

            <div className="shipment-details">
              <div className="detail-grid">
                <div className="detail-item">
                  <span className="detail-label">üè∑Ô∏è Tracking ID</span>
                  <span className="detail-value">{shipment.trackingId}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üë§ Recipient</span>
                  <span className="detail-value">{shipment.recipientName}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üì± Contact</span>
                  <span className="detail-value">{shipment.recipientPhone || 'Not provided'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üìç Delivery Address</span>
                  <span className="detail-value">{shipment.deliveryAddress}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üì¶ Package Type</span>
                  <span className="detail-value">{shipment.packageType || 'Standard'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">‚öñÔ∏è Weight</span>
                  <span className="detail-value">{shipment.weight} kg</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üìÖ Created</span>
                  <span className="detail-value">{new Date(shipment.createdAt).toLocaleString()}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üìù Special Instructions</span>
                  <span className="detail-value">{shipment.specialInstructions || 'None'}</span>
                </div>
              </div>

              <div className="status-section">
                <h3>Current Status</h3>
                <div className={`status-indicator ${getStatusClass(shipment.status)}`}>
                  <div className="status-icon">
                    {getStatusEmoji(shipment.status)}
                  </div>
                  <div className="status-text">
                    <h4>{shipment.status}</h4>
                    <p>
                      {shipment.status === 'Delivered' 
                        ? 'Your package has been successfully delivered to the recipient.'
                        : shipment.status === 'In Transit'
                        ? 'Your package is on its way to the destination.'
                        : 'Your package is being processed and will ship soon.'}
                    </p>
                    {shipment.status === 'Delivered' && (
                      <p style={{ marginTop: '8px', fontWeight: '500', color: '#2c3e50' }}>
                        Delivered on: {new Date(shipment.updatedAt).toLocaleString()}
                      </p>
                    )}
                  </div>
                </div>
              </div>

              <div className="tracking-timeline">
                <div className="timeline-card">
                  <h3>üìã Tracking Timeline</h3>
                  <div className="timeline">
                    <div className="timeline-item">
                      <div className="timeline-marker completed"></div>
                      <div className="timeline-content">
                        <h4>Package Created</h4>
                        <p>{new Date(shipment.createdAt).toLocaleString()}</p>
                      </div>
                    </div>

                    {(shipment.status === 'In Transit' || shipment.status === 'Delivered') && (
                      <div className="timeline-item">
                        <div className="timeline-marker completed"></div>
                        <div className="timeline-content">
                          <h4>Processing Complete</h4>
                          <p>Your package has been processed and is ready for transit</p>
                        </div>
                      </div>
                    )}

                    {(shipment.status === 'In Transit' || shipment.status === 'Delivered') && (
                      <div className="timeline-item">
                        <div className={`timeline-marker ${shipment.status === 'Delivered' ? 'completed' : 'active'}`}></div>
                        <div className="timeline-content">
                          <h4>In Transit</h4>
                          <p>Package is on its way to the destination</p>
                        </div>
                      </div>
                    )}

                    {shipment.status === 'Delivered' && (
                      <div className="timeline-item">
                        <div className="timeline-marker completed"></div>
                        <div className="timeline-content">
                          <h4>Delivered</h4>
                          <p>Package has been delivered successfully</p>
                          <p>{new Date(shipment.updatedAt).toLocaleString()}</p>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {shipment.qrCodeImage && (
                <div className="qr-section">
                  <h3>QR Code</h3>
                  <div className="qr-display">
                    <img 
                      src={`data:image/png;base64,${shipment.qrCodeImage}`}
                      alt="Shipment QR Code"
                      className="qr-image"
                    />
                    <p>Scan this QR code for quick access to shipment details</p>
                  </div>
                </div>
              )}
            </div>
          </div>

          <div className="action-buttons">
            <Link to="/track-shipment" className="modern-btn primary">
              Track Another Shipment
            </Link>
            {!localStorage.getItem('userId') && (
              <Link to="/login" className="modern-btn secondary">
                Sign In to Create Shipments
              </Link>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DirectTrackShipment;
