import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import apiClient from '../api/apiClient';

const CreateShipment = () => {
  const [formData, setFormData] = useState({
    recipientName: '',
    deliveryAddress: '',
    weight: '',
    packageType: '',
    specialInstructions: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validate weight
    const weight = parseFloat(formData.weight);
    if (isNaN(weight) || weight < 0.01 || weight > 1000) {
      setError('Weight must be between 0.01 and 1000 kg');
      return;
    }

    setLoading(true);

    try {
      const userId = parseInt(localStorage.getItem('userId'));
      const shipmentData = {
        recipientName: formData.recipientName,
        deliveryAddress: formData.deliveryAddress,
        weight: weight,
        packageType: formData.packageType || 'General',
        specialInstructions: formData.specialInstructions || '',
        userId
      };

      const response = await apiClient.post('/shipment', shipmentData);

      alert(`Shipment created! Tracking ID: ${response.data.trackingId}`);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create shipment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>Create New Shipment</h2>
        <button 
          onClick={() => navigate('/dashboard')}
          className="btn-secondary"
        >
          Back to Dashboard
        </button>
      </div>

      <div className="form-container">
        {error && <div className="error">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="recipientName">Recipient Name:</label>
            <input
              type="text"
              id="recipientName"
              name="recipientName"
              value={formData.recipientName}
              onChange={handleChange}
              required
              placeholder="Enter recipient's full name"
            />
          </div>

          <div className="form-group">
            <label htmlFor="deliveryAddress">Delivery Address:</label>
            <textarea
              id="deliveryAddress"
              name="deliveryAddress"
              value={formData.deliveryAddress}
              onChange={handleChange}
              required
              placeholder="Enter complete delivery address including street, city, state, and postal code"
              rows="4"
            />
          </div>

          <div className="form-group">
            <label htmlFor="weight">Weight (kg):</label>
            <input
              type="number"
              id="weight"
              name="weight"
              value={formData.weight}
              onChange={handleChange}
              required
              min="0.01"
              max="1000"
              step="0.01"
              placeholder="Enter package weight in kg (0.01 - 1000)"
            />
            <small style={{ color: '#666', fontSize: '0.875rem' }}>
              Weight must be between 0.01 kg and 1000 kg
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="packageType">Package Type:</label>
            <select
              id="packageType"
              name="packageType"
              value={formData.packageType}
              onChange={handleChange}
            >
              <option value="">Select package type (optional)</option>
              <option value="General">General</option>
              <option value="Documents">Documents</option>
              <option value="Electronics">Electronics</option>
              <option value="Clothing">Clothing</option>
              <option value="Fragile">Fragile</option>
              <option value="Perishable">Perishable</option>
              <option value="Books">Books</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="specialInstructions">Special Instructions:</label>
            <textarea
              id="specialInstructions"
              name="specialInstructions"
              value={formData.specialInstructions}
              onChange={handleChange}
              placeholder="Any special handling instructions (optional)"
              rows="3"
            />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={loading}>
              {loading ? 'Creating Shipment...' : 'Create Shipment'}
            </button>
            <button 
              type="button" 
              onClick={() => navigate('/dashboard')}
              className="btn-secondary"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>

      <div className="card" style={{ marginTop: '2rem' }}>
        <h3>What happens next?</h3>
        <ul style={{ paddingLeft: '1.5rem', lineHeight: '1.6' }}>
          <li>A unique tracking ID will be generated for this shipment</li>
          <li>A QR code will be created containing the shipment details</li>
          <li>Email and SMS notifications will be sent to you and the recipient</li>
          <li>You can track the shipment status from your dashboard</li>
          <li>Generate delivery OTP when ready to deliver</li>
        </ul>
      </div>
    </div>
  );
};

export default CreateShipment;
