import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import apiClient from '../api/apiClient';

const CreateShipment = () => {
  const [formData, setFormData] = useState({
    recipientName: '',
    deliveryAddress: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const userId = parseInt(localStorage.getItem('userId'));
      const shipmentData = {
        ...formData,
        userId
      };

      const response = await apiClient.post('/shipment', shipmentData);

      alert(`Shipment created! Tracking ID: ${response.data.trackingId}`);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create shipment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>Create New Shipment</h2>
        <button 
          onClick={() => navigate('/dashboard')}
          className="btn-secondary"
        >
          Back to Dashboard
        </button>
      </div>

      <div className="form-container">
        {error && <div className="error">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="recipientName">Recipient Name:</label>
            <input
              type="text"
              id="recipientName"
              name="recipientName"
              value={formData.recipientName}
              onChange={handleChange}
              required
              placeholder="Enter recipient's full name"
            />
          </div>
          <div className="form-group">
            <label htmlFor="deliveryAddress">Delivery Address:</label>
            <textarea
              id="deliveryAddress"
              name="deliveryAddress"
              value={formData.deliveryAddress}
              onChange={handleChange}
              required
              placeholder="Enter complete delivery address including street, city, state, and postal code"
              rows="4"
            />
          </div>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={loading}>
              {loading ? 'Creating Shipment...' : 'Create Shipment'}
            </button>
            <button 
              type="button" 
              onClick={() => navigate('/dashboard')}
              className="btn-secondary"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>

      <div className="card" style={{ marginTop: '2rem' }}>
        <h3>What happens next?</h3>
        <ul style={{ paddingLeft: '1.5rem', lineHeight: '1.6' }}>
          <li>A unique tracking ID will be generated for this shipment</li>
          <li>A QR code will be created containing the shipment details</li>
          <li>Email and SMS notifications will be sent to you and the recipient</li>
          <li>You can track the shipment status from your dashboard</li>
          <li>Generate delivery OTP when ready to deliver</li>
        </ul>
      </div>
    </div>
  );
};

export default CreateShipment;
