import React, { useState } from 'react';
import apiClient from '../api/apiClient';

const TrackShipment = () => {
  const [trackingId, setTrackingId] = useState('');
  const [shipment, setShipment] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!trackingId.trim()) {
      setError('Please enter a tracking ID');
      return;
    }

    setError('');
    setLoading(true);
    setSearched(true);
    setShipment(null);

    try {
      const response = await apiClient.get(`/shipment/tracking/${trackingId.trim()}`);
      setShipment(response.data);
    } catch (err) {
      if (err.response?.status === 404) {
        setError('Shipment not found. Please check your tracking ID and try again.');
      } else {
        setError(err.response?.data?.message || 'An error occurred while tracking the shipment.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setTrackingId('');
    setShipment(null);
    setError('');
    setSearched(false);
  };

  return (
    <div>
      <h2>Track Your Shipment</h2>
      <p style={{ marginBottom: '2rem', color: '#666' }}>
        Enter your tracking ID below to check the status of your shipment.
      </p>

      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="trackingId">Tracking ID:</label>
            <input
              type="text"
              id="trackingId"
              value={trackingId}
              onChange={(e) => setTrackingId(e.target.value)}
              placeholder="Enter your tracking ID (e.g., TRK123456)"
              required
            />
          </div>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={loading}>
              {loading ? 'Tracking...' : 'Track Shipment'}
            </button>
            {searched && (
              <button 
                type="button" 
                onClick={handleReset}
                className="btn-secondary"
              >
                Reset
              </button>
            )}
          </div>
        </form>
      </div>

      {loading && (
        <div className="loading">
          <div className="spinner"></div>
          <p>Searching for your shipment...</p>
        </div>
      )}

      {error && <div className="error">{error}</div>}

      {shipment && (
        <div className="tracking-result">
          <div className="card">
            <h3>Shipment Found!</h3>
            <div className="details">
              <p><strong>Tracking ID:</strong> {shipment.trackingId}</p>
              <p><strong>Recipient Name:</strong> {shipment.recipientName}</p>
              <p><strong>Delivery Address:</strong> {shipment.deliveryAddress}</p>
              <p><strong>Status:</strong> 
                <span 
                  style={{
                    marginLeft: '0.5rem',
                    padding: '0.25rem 0.5rem',
                    borderRadius: '4px',
                    fontSize: '0.875rem',
                    fontWeight: '500',
                    backgroundColor: shipment.status === 'Delivered' ? '#d4edda' : '#fff3cd',
                    color: shipment.status === 'Delivered' ? '#155724' : '#856404'
                  }}
                >
                  {shipment.status}
                </span>
              </p>
              <p><strong>Created At:</strong> {new Date(shipment.createdAt).toLocaleString()}</p>
              {shipment.deliveredAt && (
                <p><strong>Delivered At:</strong> {new Date(shipment.deliveredAt).toLocaleString()}</p>
              )}
            </div>

            {shipment.qrCodeImage && (
              <div className="qr-code">
                <h4>QR Code</h4>
                <img 
                  src={`data:image/png;base64,${shipment.qrCodeImage}`}
                  alt="Shipment QR Code"
                  width="150"
                  height="150"
                  style={{ border: '1px solid #ddd', borderRadius: '8px' }}
                />
                <p style={{ fontSize: '0.875rem', color: '#666', marginTop: '0.5rem' }}>
                  Scan this QR code for quick access to shipment details
                </p>
              </div>
            )}

            {shipment.status === 'Delivered' && (
              <div 
                style={{
                  backgroundColor: '#d4edda',
                  color: '#155724',
                  padding: '1rem',
                  borderRadius: '4px',
                  textAlign: 'center',
                  marginTop: '1rem'
                }}
              >
                <strong>✓ Shipment Successfully Delivered!</strong>
                {shipment.deliveredAt && (
                  <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.875rem' }}>
                    Delivered on {new Date(shipment.deliveredAt).toLocaleString()}
                  </p>
                )}
              </div>
            )}

            {shipment.status !== 'Delivered' && (
              <div 
                style={{
                  backgroundColor: '#fff3cd',
                  color: '#856404',
                  padding: '1rem',
                  borderRadius: '4px',
                  textAlign: 'center',
                  marginTop: '1rem'
                }}
              >
                <strong>📦 Shipment In Transit</strong>
                <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.875rem' }}>
                  Your shipment is on its way and will be delivered soon.
                </p>
              </div>
            )}
          </div>
        </div>
      )}

      {searched && !shipment && !loading && !error && (
        <div className="card">
          <h3>No Results</h3>
          <p>No shipment found with the tracking ID "{trackingId}". Please check the tracking ID and try again.</p>
        </div>
      )}

      <div className="card" style={{ marginTop: '2rem' }}>
        <h3>Need Help?</h3>
        <ul style={{ paddingLeft: '1.5rem', lineHeight: '1.6' }}>
          <li>Make sure you've entered the correct tracking ID</li>
          <li>Tracking IDs are usually in the format TRK followed by numbers</li>
          <li>Check your email for the tracking ID if you can't find it</li>
          <li>Contact customer support if you continue to have issues</li>
        </ul>
      </div>
    </div>
  );
};

export default TrackShipment;
